$(document).ready(function(){
  var CHECKIE6 = function() {
    return new RegExp("MSIE 6").test(navigator.userAgent);
  };
  var ie=0;var ie4=0;var ie5=0;var ie6=0;var com=0;var dcm;
  
  if (CHECKIE6()) {
    return false;
  }
  else {
    var current = $('.current').index();
    var item = $('.nav ul.item');
    item.each(function(i){
      $('.item').find('#sub_id').removeClass('show').addClass('select_sub');
      $('.item').addClass('select').removeClass('current');
      $(this).find('#sub_id').addClass('show').removeClass('select_sub');
      $(this).addClass('current').removeClass('select');
      var supWidth = $(this).find('.level-one a').width(); 
      var supLeft = $(this).find('.level-one a').offset().left;
      
      var subWidth = 0;
      $subLI = $(this).find('li');
      $subLI.each(function(li){
    	   subWidth += $(this).width();
      });
      
      var subLeft = $(this).find('#sub_id').offset().left;
      var width = $(this).find('#sub_id').width();
      var temp = (supWidth / 2 + supLeft) - ((subLeft + subWidth) / 2);
      var padLeft = temp < 0 ? 0 : temp;
      $(this).find('#sub_id').css('padding-left', padLeft);
      $(this).find('#sub_id').css('width', width - padLeft);
      
    });
    $('.item').find('#sub_id').removeClass('show').addClass('select_sub');
    $('.item').addClass('select').removeClass('current');
    $('.item:eq(' + current + ')').find('#sub_id').addClass('show').removeClass('select_sub');
    $('.item:eq(' + current + ')').addClass('current').removeClass('select');
  }
  /*=== V-menu ===*/
 $(".item-menu")
      .mouseover(function(){$(this).find("a").css({"color":"#b60b00"});})
      .mouseout(function(){$(this).find("a").css({"color":"#004784"});})
});
